];

function reducer( state = 기본state, 액션) {
  if ( 액션.type === '항목추가') {
    let copy = [ ...state];
    copy.push(액션.payload);
    return copy

  } else if (액션.type === '수량증가') {
    let copy = [ ...state];
    copy[0].quan++;
    return copy
  } else if (액션.type === '수량감소') {
    let copy = [ ...state];
    copy[0].quan--;
    return copy
  } else{
    return state
  }
}

let store = createStore(combineReducers({reducer, reducer2}));



ReactDOM.render(
